<?PHP//Check for access right// $v_access = \E::get('obj_curr_module')->get_right_to_access('read');// if(!$v_access){// 	\V::append_data( 'body', \V::load_box( \E::get( 'obj_curr_module_lang')->get('MODULE_AUTHORISED_ERROR'), \V::load_view( $this->get_view_file_path( 'msg_not_authorised', 'share' ), $v_object, $v_var ), 'box_form.php' ) );// 	\V::output_view();// 	exit;// }$v_var = null;$v_param = $this->get_param();$v_id = $v_param[0];$v_cond['usr_id'] = $v_id;$v_user_info = \E::get( 'obj_system_module')->user_get($v_cond);if(!$v_user_info || !is_array($v_user_info)){	header('location: '. \E::get('obj_curr_module')->build_action_path('user','list'));	exit();}$v_var = array(				'user_type' => $v_user_info['usr_from'],				'user_info' => $v_user_info,				'user_phone' => $v_user_info['phone_office'],				'user_date_modified' =>$v_user_info['_rec_modified_on']				);				$v_var["created_by"]= (isset($v_user_info["_rec_created_by"]) && $v_user_info["_rec_created_by"]!="")? $v_user_info["_rec_created_by"]:"";$v_var["modified_by"]= (isset($v_user_info["_rec_modified_by"]) && $v_user_info["_rec_modified_by"]!="")? $v_user_info["_rec_modified_by"]:"";$v_var["created_on"]= (isset($v_user_info["_rec_created_on"]) && $v_user_info["_rec_created_on"]!="")?\E::get( "obj_curr_module")->__to_display_date($v_user_info["_rec_created_on"],"date"):"";$v_var["modified_on"]= (isset($v_user_info["_rec_modified_on"]) && $v_user_info["_rec_modified_on"]!="")?\E::get( "obj_curr_module")->__to_display_date($v_user_info["_rec_modified_on"],"date"):"";$v_user_acl_infor = \E::get( 'obj_curr_module')->user_acl_get($v_id);$v_user_group_infor = \E::get( 'obj_curr_module')->user_group_get($v_user_acl_infor[0]['group_id']);$v_var['usr_group'] = $v_user_group_infor[0]['kwd_value'][\E::get('obj_curr_module')->get_lang()];$v_var['id'] = $v_id;\V::append_data( 'menu', \V::load_view( $this->get_view_file_path( 'menu', 'share' ), $v_object ) );\V::append_data( 'body', \V::load_view( $this->get_view_file_path(), $v_object, $v_var ) );\V::output_view();?>