<?PHP//Check for access right$v_access = \E::get('obj_curr_module')->get_right_to_access('delete');if(!$v_access){	\V::append_data( 'body', \V::load_box( \E::get( 'obj_curr_module_lang')->get('MODULE_AUTHORISED_ERROR'), \V::load_view( $this->get_view_file_path( 'msg_not_authorised', 'share' ), $v_object, $v_var ), 'box_form.php' ) );	\V::output_view();	exit;}$v_del_method = '0'; //0 = soft delete, 1 = hard delete$v_param = $this->get_param();$v_group_id = (isset($v_param[0]) ? $v_param[0] : '');if($v_group_id==""){	header('location: '. \E::get('obj_curr_module')->build_action_path('user_group','list'));	exit(0);}$v_delete['id'] = $v_group_id;if(\E::get( 'obj_curr_module')->user_group_del($v_delete, $v_del_method)){	$v_var['del_error']=false;	$v_var['callback_url']=\E::get('obj_curr_module')->build_action_path('user_group','list');}else{	$v_var['del_error']=true;	$v_var['callback_url']=\E::get('obj_curr_module')->build_action_path('user_group','list');}header('location: '. \E::get('obj_curr_module')->build_action_path('user_group','list'));exit();?>