<script language="javascript">$(document).ready(	function()	{		document.getElementById("timeCount").innerHTML="<?php echo \E::get("obj_kernel_config")->get("general/redirect_in_second") ?> ";	})function redirectpage(){	window.location="<?php echo MOONLIGHT_DNS_PATH . MOONLIGHT_FOLDER_PATH; ?>";}function countdown(){	a = document.getElementById("timeCount").innerHTML;	b = parseInt(a,10)-1;	if (b>0){		document.getElementById("timeCount").innerHTML=" " + b + " ";	}else{		if (b==0){			redirectpage();		}	}}setInterval("countdown()",1000);</script><center><table border=0 cellpadding=10 cellspacing=0 width=70%><tr><td width=20% align=right><?PHP echo \E::get('obj_curr_module')->get_image_tag('warning.gif') ?></td><td><?PHP echo str_replace("<MAIL>",						\E::get("obj_kernel_config")->get("general/admin_email"),						\E::get('obj_curr_module_lang')->get('MODULE_NOT_AUTHORISED')); ?><br><br><?PHP echo \E::get('obj_curr_module_lang')->get('MODULE_REDIRECT_MSG') ?></td></tr></table></center>